import React, { Component } from 'react'
import axios from 'axios';
export class PostList extends Component {
    constructor(props) {
        super(props)

        this.state = {
             posts : [] ,
             error : ' '
        }
    }
    componentDidMount(){
        axios.get('https://newsapi.org/v2/everything?q=bitcoin&from=2020-01-12&sortBy=publishedAt&apiKey=a2f39b1fb611435caaf709ba7aa5f86e').then(response =>{
            console.log(response)
            this.setState({posts : response.data})
        })
        .catch(error=>{
            console.log(error) 
            this.setState({errorMsg : 'error in data'})
        })

    }

    render() {
        const {posts, errorMsg}= this.state
        return (
            <div>
             list of posts
             {

                 posts.map(post => 
                    <div>
                      <center>
                      <li key={post.id}>
                        Name: {post.name} | Email: {post.email} | Author : {post.author} 
                    </li>
                    </center>  


                    </div>

                )

             }   
             {
                 errorMsg? <div>{errorMsg}</div>: null
             }
            </div>
        )
    }
}

export default PostList
