import React, { Component } from 'react';
import {MDBIframe, MDBBtn,  MDBCardBody, MDBCardImage,  MDBCardText, MDBCol,MDBModal, MDBModalBody, MDBModalHeader, MDBModalFooter  } from 'mdbreact';
import axios from 'axios';

class PostList extends Component {
  constructor(props) {
    // Pass props to parent class
    super(props);
    // Set initial state
    this.state = {
      articles: [],
      modal: false,
      isLoaded: false
    };


  }
  toggle = () => {
    this.setState({
      modal: !this.state.modal
    });
  }
  componentDidMount() {


    // Make HTTP reques with Axios
    axios
      .get(`https://newsapi.org/v2/top-headlines?country=in&apiKey=a2f39b1fb611435caaf709ba7aa5f86e`)
      .then(res => {
        const articles = res.data.articles;

        console.log(articles);
        this.setState({ articles: articles ,  isLoaded: true });
      })
      .catch(error => {
        console.log(error);
      });
}

  formatDate(date) {
    var time = new Date(date);
    var year = time.getFullYear();
    var day = time.getDate();
    var hour = time.getHours();
    var minute = time.getMinutes();
    var month = time.getMonth() + 1;
    var composedTime = day + '/' + month + '/' + year + ' | ' + hour + ':' + (minute < 10 ? '0' + minute : minute);
    return composedTime;
  }



  render() {
    const { isLoaded} = this.state;

    if (!isLoaded)
        return <div><center>Loading.....</center></div>;
    return (
      <div>


           <MDBCol className="cardsContainer">

        {this.state.articles.map((news, i) => {

          return (
            <div className="card" key={i}>


              <div className="content">
              <MDBModal isOpen={this.state.modal} toggle={this.toggle}>
        <MDBModalHeader toggle={this.toggle}>MDBModal title</MDBModalHeader>
        <MDBModalBody>
        <MDBIframe src={news.url} />
        </MDBModalBody>
        <MDBModalFooter>
          <MDBBtn color="secondary" onClick={this.toggle}>Close</MDBBtn>

        </MDBModalFooter>
      </MDBModal>
                <h3>
                  <a href={news.url} target="_blank" rel="noopener noreferrer">
                    {news.title}
                  </a>
                </h3>
                <MDBCardBody>{news.description}</MDBCardBody>
                <div className="author">
                  <MDBCardText>
                    By <i>{news.author ? news.author : this.props.default}</i>
                  </MDBCardText>
                  <MDBCardText>{this.formatDate(news.publishedAt)}</MDBCardText>
                  <MDBBtn href={news.url}>view</MDBBtn>
                  <MDBBtn onClick={this.toggle}>Modal</MDBBtn>
                </div>
              </div>
              <div className="image">
                <MDBCardImage src={news.urlToImage} alt="" />
              </div>
            </div>
          );
        })}
      </MDBCol>

      </div>

    );
  }
}

export default PostList;
