import React, { Component } from 'react'
import { MDBBtn, MDBCard, MDBCardBody, MDBCardImage, MDBCardTitle, MDBCardText, MDBCol } from 'mdbreact';

import axios from 'axios';
export class PostList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            articles: []
        }
    }
    componentDidMount(){
        axios.get(`https://newsapi.org/v2/top-headlines?country=in&apiKey=a2f39b1fb611435caaf709ba7aa5f86e`)
        .then(res => {
          const articles = res.data.articles;
          // Set state with result
          console.log(articles);
          this.setState({ articles: articles });
        })
        .catch(error => {
          console.log(error);
        });

    }

  formatDate(date) {
    var time = new Date(date);
    var year = time.getFullYear();
    var day = time.getDate();
    var hour = time.getHours();
    var minute = time.getMinutes();
    var month = time.getMonth() + 1;
    var composedTime = day + '/' + month + '/' + year + ' | ' + hour + ':' + (minute < 10 ? '0' + minute : minute);
    return composedTime;
  }

    render() {
        const {posts, errorMsg}= this.state
        return (

            <div className="cardsContainer">
            {this.state.articles.map((news, i) => {
              return (
                <div className="card" key={i}>

                  <MDBCol>
            <MDBCard style={{ width: "22rem" }} key={i}>
              <MDBCardImage className="img-fluid" src={news.urlToImage} waves />
              <MDBCardBody>
                <MDBCardTitle>{news.title}</MDBCardTitle>
                <MDBCardText>
                {news.description}
                      <p>{this.formatDate(news.publishedAt)}</p>
                </MDBCardText>
                <MDBBtn href={news.url}>view more..</MDBBtn>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
                </div>


              );
            })}
          </div>
        )
    }
}

export default PostList
