import React, { Component } from 'react';
import { MDBBtn,  MDBCardBody, MDBCardImage,  MDBCardText, MDBCol } from 'mdbreact';
import axios from 'axios';
import {
  MDBCarousel,
  MDBCarouselCaption,
  MDBCarouselInner,
  MDBCarouselItem,
  MDBView,
  MDBMask,
  MDBContainer
} from 'mdbreact';
class Poster extends Component {
  constructor(props) {
    // Pass props to parent class
    super(props);
    // Set initial state
    this.state = {
      articles: [],
      isLoaded: false
    };


  }

  componentDidMount() {


    // Make HTTP reques with Axios
    axios
      .get(`https://newsapi.org/v2/top-headlines?country=in&apiKey=a2f39b1fb611435caaf709ba7aa5f86e`)
      .then(res => {
        const articles = res.data.articles;

        console.log(articles);
        this.setState({ articles: articles ,  isLoaded: true });
      })
      .catch(error => {
        console.log(error);
      });
}

  formatDate(date) {
    var time = new Date(date);
    var year = time.getFullYear();
    var day = time.getDate();
    var hour = time.getHours();
    var minute = time.getMinutes();
    var month = time.getMonth() + 1;
    var composedTime = day + '/' + month + '/' + year + ' | ' + hour + ':' + (minute < 10 ? '0' + minute : minute);
    return composedTime;
  }



  render() {
    const { isLoaded} = this.state;

    if (!isLoaded)
        return <div><center>Loading.....</center></div>;
    return (
      <div>

<MDBContainer header='With Captions'>
            <MDBCarousel

              showControls
              showIndicators
              className='z-depth-1'
              slide
            >
              <MDBCarouselInner>
                <MDBCarouselItem itemId='1'>
                  <MDBView>
                    <img
                      className='d-block w-100'
                      src='https://mdbootstrap.com/img/Photos/Slides/img%20(68).jpg'
                      alt='First slide'
                    />
                    <MDBMask overlay='black-light' />
                  </MDBView>
                  <MDBCarouselCaption>
                    <h3 className='h3-responsive'>Light mask</h3>
                    <p>First text</p>
                  </MDBCarouselCaption>
                </MDBCarouselItem>

              </MDBCarouselInner>
            </MDBCarousel>
          </MDBContainer>

           <MDBCol className="cardsContainer">

        {this.state.articles.map((news, i) => {

          return (
         <h1> </h1>
          );
        })}
      </MDBCol>

      </div>

    );
  }
}

export default Poster;
