import React, { Component } from 'react'
import axios from 'axios';
export class PostList extends Component {
    constructor(props) {
        super(props)

        this.state = {
             posts : [] ,
             persons: [],
             error : ' '
        }
    }
    componentDidMount(){
        axios.get('https://newsapi.org/v2/everything?q=bitcoin&from=2020-01-12&sortBy=publishedAt&apiKey=a2f39b1fb611435caaf709ba7aa5f86e')
        .then(response =>{
            console.log(response)
            const persons = response.data;
             this.setState({ persons });
        })
        .catch(error=>{
            console.log(error) 
            this.setState({errorMsg : 'error in data'})
        })

    }

    render() {
        const {posts, errorMsg}= this.state
        return (
            <div>
             list of posts
             {
                 posts.length ?
                 posts.map(posts => <div key ={posts.id}>{posts.title}</div>):
                 null


             } 
              { this.state.persons.map(person => <li>{person.name}</li>)}  
             {
                 errorMsg? <div>{errorMsg}</div>: null
             }
            </div>
        )
    }
}

export default PostList
